var link=document.querySelector(".button-connection"),popup=document.querySelector(".modal-connection"),close=popup.querySelector(".modal-close"),form=popup.querySelector(".connection-form"),nameFamily=popup.querySelector("[name=name]"),email=popup.querySelector("[name=email]"),text=popup.querySelector("[name=text]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),nameFamily.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(e){nameFamily.value&&email.value&&text.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))});